import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and enter a search term
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');

    // Click the search button
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Wait for the sort dropdown to be visible
    await page.waitForSelector('select.search-category');

    // Select 'Price: Low to High' from the sort dropdown
    await page.selectOption('select.search-category', { value: 'price_low_to_high' }); // Assuming this is the value for sorting

    // Wait for the products to be sorted
    await page.waitForTimeout(1000); // Adjust if necessary for dynamic content

    // Take a screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract data to verify task completion (e.g., product prices)
    const productPrices = await page.$$eval('.products-grid .product-card .price', prices => prices.map(price => price.innerText));

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: { productPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

