    // Take screenshot after search results are displayed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Wait for the sort dropdown to be available and select 'Price: Low to High'
    await page.waitForSelector('select.search-category');
    await page.selectOption('select.search-category', { label: 'Price: Low to High' });

    // Wait for the products to be sorted
    await page.waitForTimeout(1000); // Adjust timing as necessary for loading

    // Take screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract data to verify sorting
    const sortedProducts = await page.$$eval('.products-grid .product-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.product-title')?.innerText || '';
        const price = card.querySelector('.product-price')?.innerText || '';
        return { title, price };
      });
    });

    return {
      success: true,
      action: "Searched for products and sorted by Price: Low to High",
      extractedData: sortedProducts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and type the search query
    await page.waitForSelector('#searchInput', { state: 'visible' });
    await page.fill('#searchInput', 'electronics');

    // Click the search button
    await page.waitForSelector('button.search-btn', { state: 'visible' });
    await page.click('button.search-btn');

    // Wait for the product grid to load
    await page.waitForSelector('.products-grid', { state: 'visible' });

    // Take a screenshot of the product grid after loading
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_grid.png'));

    // Wait for the sort dropdown to be visible
    await page.waitForSelector('select.search-category', { state: 'visible' });

    // Click the sort dropdown and select "Price: Low to High"
    await page.selectOption('select.search-category', { value: 'price_low_to_high' });

    // Wait for the products to be sorted
    await page.waitForTimeout(2000); // Adjusting for dynamic loading

    // Take a final screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_products.png'));

    // Verify that sorting was successful by checking the first product's price
    const firstProductPrice = await page.innerText('.products-grid .product-card:first-child .price');

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: { firstProductPrice },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
