import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be available and type in a search query
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');

    // Click the search button
    await page.waitForSelector('button.search-btn');
    await page.click('button.search-btn');

    // Wait for products to load
    await page.waitForSelector('.products-grid');

    // Take a screenshot of the loaded products
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_loaded_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_loaded_products.png'));

    // Wait for the sort dropdown and select 'Price: Low to High'
    await page.waitForSelector('select.search-category');
    await page.selectOption('select.search-category', 'price_low_to_high');

    // Take a screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_products.png'));

    // Verify that products are sorted by price
    const sortedProducts = await page.$$eval('.products-grid .product-card', cards => {
      return cards.map(card => {
        const priceText = card.querySelector('.product-price').innerText;
        return parseFloat(priceText.replace(/[^0-9.-]+/g,""));
      });
    });

    const isSorted = sortedProducts.every((price, index) => index === 0 || price >= sortedProducts[index - 1]);

    return {
      success: true,
      action: "Applied price sort filter to products",
      extractedData: { isSorted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
