import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and enter a search term
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');

    // Click the search button
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after search results are displayed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Wait for the sort dropdown to be visible and select 'Price: Low to High'
    await page.waitForSelector('select[name="undefined"]');
    await page.selectOption('select[name="undefined"]', { label: 'Price: Low to High' });

    // Wait for the sorting to be applied
    await page.waitForLoadState('networkidle');

    // Take a screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract product titles and prices to verify sorting
    const products = await page.$$eval('.products-grid .product-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('h2') ? card.querySelector('h2').innerText : '';
        const price = card.querySelector('.price') ? card.querySelector('.price').innerText : '';
        return { title, price };
      });
    });

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: products,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

