import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and enter a search term
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');

    // Click on the search button
    await page.waitForSelector('button.search-btn');
    await page.click('button.search-btn');

    // Wait for the products grid to load
    await page.waitForSelector('.products-grid');

    // Take a screenshot of the products grid
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_products_grid.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_products_grid.png'));

    // Wait for the sort dropdown to be visible and select 'Price: Low to High'
    await page.waitForSelector('select[name="undefined"]');
    await page.selectOption('select[name="undefined"]', { label: 'Price: Low to High' });

    // Wait for the products to be sorted
    await page.waitForTimeout(2000); // Adjust timeout as necessary

    // Take a screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_products.png'));

    // Extract product data to verify sorting
    const sortedProducts = await page.$$eval('.products-grid .product-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.product-title').innerText,
        price: card.querySelector('.product-price').innerText
      }));
    });

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: { sortedProducts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

