import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for a product
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'laptop');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after searching
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Wait for the sort dropdown and select 'Price: Low to High'
    await page.waitForSelector('select.search-category');
    await page.selectOption('select.search-category', 'all'); // Ensure the category is set to 'All'

    await page.waitForSelector('.products-grid .product-card', { timeout: 5000 });

    await page.selectOption('select[name="undefined"]', 'price_low_to_high'); // Selecting price sort

    // Wait for the products to sort
    await page.waitForTimeout(2000); // Wait for sorting to take effect

    // Take a screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract data to verify sorting
    const sortedProductPrices = await page.$$eval('.products-grid .product-card .price', prices => prices.map(price => price.textContent));

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: { sortedPrices: sortedProductPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
