import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 2: Enter a search term and perform search
    // Wait for search input
    await page.waitForSelector('#searchInput', { timeout: 5000 });
    await page.fill('#searchInput', 'laptop');
    // Wait for search button and click
    await page.waitForSelector('button.search-btn', { timeout: 5000 });
    await page.click('button.search-btn');

    // Wait for products grid to appear (assume .products-grid or .product-card)
    await page.waitForSelector('.products-grid .product-card, .product-card', { timeout: 7000 });

    // Screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Step 3: Locate and interact with the sort dropdown
    // Try common selectors for sort dropdown
    let sortSelectHandle = null;
    // Try by id, class, name, or label text
    const sortSelectors = [
      'select#sortSelect',
      'select.sortSelect',
      'select[name="sort"]',
      'select[name="sortSelect"]',
      'select[aria-label*="Sort"]',
      'select:has-text("Price")',
      'select'
    ];
    for (const selector of sortSelectors) {
      try {
        sortSelectHandle = await page.$(selector);
        if (sortSelectHandle) {
          // Check if it contains the option for "Price: Low to High"
          const options = await sortSelectHandle.$$eval('option', opts => opts.map(o => o.textContent));
          if (options.some(o => o && o.toLowerCase().includes('price') && o.toLowerCase().includes('low'))) {
            break;
          } else {
            sortSelectHandle = null;
          }
        }
      } catch (e) {/* ignore */}
    }

    if (!sortSelectHandle) {
      throw new Error('Sort dropdown not found');
    }

    // Select "Price: Low to High"
    const sortOptions = await sortSelectHandle.$$('option');
    let found = false;
    for (const option of sortOptions) {
      const text = (await option.textContent()) || '';
      if (text.toLowerCase().includes('price') && text.toLowerCase().includes('low')) {
        const value = await option.getAttribute('value');
        await sortSelectHandle.selectOption(value);
        found = true;
        break;
      }
    }
    if (!found) {
      throw new Error('Sort option "Price: Low to High" not found');
    }

    // Wait for products to re-render (wait for network or DOM change)
    await page.waitForTimeout(1500); // fallback, in case no network

    // Screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_sort.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_sort.png'));

    // Step 4: Extract product prices to verify sorting
    // Get all visible product cards
    const productCards = await page.$$('.products-grid .product-card, .product-card');
    const prices = [];
    for (const card of productCards.slice(0, 5)) { // check first 5 for brevity
      // Try to find price element inside card
      const priceEl = await card.$(':text-matches("[$â‚¬Â£Â¥]?[0-9,.]+", "i")');
      let priceText = '';
      if (priceEl) {
        priceText = await priceEl.textContent();
      } else {
        // Try common price selectors
        const priceAlt = await card.$('.price, .product-price, [class*="price"], [data-testid*="price"]');
        if (priceAlt) {
          priceText = await priceAlt.textContent();
        }
      }
      // Extract number
      const match = priceText && priceText.match(/([0-9]+[.,][0-9]{2})/);
      if (match) {
        prices.push(parseFloat(match[1].replace(/,/g, '')));
      }
    }

    // Check if prices are sorted ascending
    let isSorted = false;
    if (prices.length > 1) {
      isSorted = prices.every((v, i, arr) => i === 0 || arr[i - 1] <= v);
    }

    return {
      success: isSorted,
      action: "Searched for products and applied 'Price: Low to High' sort filter",
      extractedData: {
        prices: prices,
        isSortedAscending: isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const step1 = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: step1 });
    screenshots.push(step1);

    // --- 1. Perform a product search (use the search input and button) ---

    // Wait for search input using alternative selector (input with placeholder)
    const searchInputSelector = 'input[placeholder="Search Amazon"]';
    await page.waitForSelector(searchInputSelector, { timeout: 5000 });

    // Enter a generic query ("phone") to ensure there are results
    await page.fill(searchInputSelector, 'phone');

    // Wait a bit for any autosuggest (not strictly needed)
    await page.waitForTimeout(300);

    // Find the search button via its aria-label or icon (alternative to .search-btn)
    // Try: button containing a search icon
    const searchBtnXPath = '//button[.//i[contains(@class,"fa-search")]]';
    await page.waitForSelector(searchBtnXPath, { timeout: 4000 });
    await page.click(searchBtnXPath);

    // Wait for product grid to appear (give more time for JS)
    const productsGridSelector = '.products-grid, #productsGrid, [class*="product-grid"]';
    await page.waitForSelector(productsGridSelector, { timeout: 5000 });

    // Screenshot after search
    const step2 = path.join(screenshotsDir, 'step_2_after_search.png');
    await page.screenshot({ path: step2 });
    screenshots.push(step2);

    // --- 2. Find and interact with the sort dropdown ---

    // Try to find the sort dropdown using several strategies:
    // - Check for select[name], select with option containing "Price"
    // - Use XPath to locate a select with at least one option mentioning 'Price'

    // Wait for any select with some sort options
    const sortSelectXPath = '//select[option[contains(translate(text(), "ABCDEFGHIJKLMNOPQRSTUVWXYZ", "abcdefghijklmnopqrstuvwxyz"), "price")]]';
    await page.waitForSelector(sortSelectXPath, { timeout: 4000 });

    // Find the "Price: Low to High" option value
    const options = await page.$$eval(`${sortSelectXPath}/option`, opts =>
      opts.map(opt => ({ value: opt.value, text: opt.textContent }))
    );
    let priceLowToHighValue = null;
    for (const opt of options) {
      if (opt.text.toLowerCase().includes('price') && opt.text.toLowerCase().includes('low to high')) {
        priceLowToHighValue = opt.value;
        break;
      }
    }
    // If not found, fallback: select option containing "Price: Low to High" by text
    if (!priceLowToHighValue) {
      priceLowToHighValue = await page.evaluate(() => {
        const sel = document.querySelector('select');
        if (!sel) return null;
        for (const opt of sel.options) {
          if (opt.textContent.toLowerCase().includes('price') && opt.textContent.toLowerCase().includes('low to high')) {
            return opt.value;
          }
        }
        return null;
      });
    }

    // Select "Price: Low to High" in the sort dropdown
    if (priceLowToHighValue) {
      await page.selectOption(sortSelectXPath, priceLowToHighValue);
    } else {
      // Fallback: try to click the option directly
      await page.selectOption(sortSelectXPath, { label: /Price.*Low.*High/i });
    }

    // Wait for products to re-render (wait for one product card to change)
    // We'll take a snapshot of product prices before & after to confirm sort order

    // Wait for short time for sorting to apply
    await page.waitForTimeout(1200);

    // Screenshot after sort applied
    const step3 = path.join(screenshotsDir, 'step_3_after_sort.png');
    await page.screenshot({ path: step3 });
    screenshots.push(step3);

    // --- 3. Extract product prices to verify sorting ---

    // Try different selectors for product cards and price elements
    const productCardSelector = '.product-card, [class*="product-card"], .products-grid .product-card, .products-grid > div';
    await page.waitForSelector(productCardSelector, { timeout: 4000 });

    // Extract up to 8 product prices
    const productData = await page.$$eval(productCardSelector, cards => {
      return cards.slice(0, 8).map(card => {
        // Attempt to find price in span/class containing 'price'
        let priceEl = card.querySelector('.price') ||
                      card.querySelector('[class*="price"]') ||
                      Array.from(card.querySelectorAll('span')).find(el => el.textContent && el.textContent.match(/\$\d/));
        let priceText = priceEl ? priceEl.textContent : '';
        // Remove $ and parse as float
        let price = parseFloat(priceText.replace(/[^0-9.]/g, ''));
        return { price, priceText: priceText.trim() };
      }).filter(item => !isNaN(item.price));
    });

    // Verify sorted (low to high)
    const sorted = productData.every((item, i, arr) => i === 0 || item.price >= arr[i - 1].price);

    return {
      success: true,
      action: `Searched for a product and applied 'Price: Low to High' sort filter. Product prices after sort: ${productData.map(p => p.priceText).join(', ')}.`,
      extractedData: {
        prices: productData.map(p => p.price),
        sortedLowToHigh: sorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    const initialShot = path.join(screenshotsDir, 'step_1_initial.png');
    await page.screenshot({ path: initialShot });
    screenshots.push(initialShot);

    // Wait for the search input using a different selector strategy (.search-input instead of #searchInput)
    await page.waitForSelector('input.search-input');

    // Type a search query (use 'Sony' as a medium result term)
    await page.fill('input.search-input', 'Sony');

    // Wait for the search button by its icon (alternative: button with .search-btn and child i.fa-search)
    const searchBtnSelector = 'button.search-btn';
    await page.waitForSelector(searchBtnSelector);

    // Click the search button
    await page.click(searchBtnSelector);

    // Wait for products grid to appear using a container selector
    await page.waitForSelector('.products-grid, .products-list, .product-card', { timeout: 5000 });

    // Take screenshot after search results are loaded
    const searchResultShot = path.join(screenshotsDir, 'step_2_after_search.png');
    await page.screenshot({ path: searchResultShot });
    screenshots.push(searchResultShot);

    // Wait for a sort/filter dropdown to appear.
    // Try select[name], select[class*="sort"], select:has(option:has-text("Price: Low to High"))
    let sortDropdown = null;
    const possibleSelectors = [
      'select.sort-select',
      'select[name="sort"]',
      'select[class*="sort"]',
      'select:has(option:text("Price: Low to High"))',
      'select'
    ];

    for (const sel of possibleSelectors) {
      try {
        sortDropdown = await page.waitForSelector(sel, { timeout: 1500 });
        // Confirm it contains "Price: Low to High"
        const options = await sortDropdown.evaluate(node =>
          Array.from(node.options).map(opt => opt.textContent)
        );
        if (options.some(opt => opt.toLowerCase().includes('price: low to high'))) {
          break;
        }
        sortDropdown = null;
      } catch (e) {
        // continue to next selector
      }
    }

    // If not found by previous means, fallback: find select with option text
    if (!sortDropdown) {
      const selects = await page.$$('select');
      for (const sel of selects) {
        const options = await sel.evaluate(node =>
          Array.from(node.options).map(opt => opt.textContent)
        );
        if (options.some(opt => opt.toLowerCase().includes('price: low to high'))) {
          sortDropdown = sel;
          break;
        }
      }
    }

    if (!sortDropdown) {
      throw new Error('Sort dropdown with "Price: Low to High" not found');
    }

    // Select the "Price: Low to High" option by its visible text
    // Get the value for that option
    const sortValue = await sortDropdown.evaluate(node => {
      const opt = Array.from(node.options).find(
        o => o.textContent.toLowerCase().includes('price: low to high')
      );
      return opt ? opt.value : null;
    });

    if (!sortValue) {
      throw new Error('"Price: Low to High" option not found in sort dropdown');
    }

    // Select the option
    await sortDropdown.selectOption(sortValue);

    // Wait for products to re-sort (wait for DOM changes)
    await page.waitForTimeout(1200);

    // Take screenshot after applying price sort
    const afterSortShot = path.join(screenshotsDir, 'step_3_after_sort.png');
    await page.screenshot({ path: afterSortShot });
    screenshots.push(afterSortShot);

    // Extract product prices from first 5 results to verify sort
    const priceTexts = await page.$$eval('.product-card', nodes =>
      nodes.slice(0, 5).map(card => {
        // Try to find price inside the card
        const priceEl = card.querySelector('.product-price, .price, .product-card-price, [class*="price"]');
        return priceEl ? priceEl.textContent.trim() : '';
      })
    );

    // Convert price texts to numbers (handle $ and commas)
    const prices = priceTexts
      .map(txt => {
        const match = txt.match(/[\d,.]+/);
        return match ? parseFloat(match[0].replace(/,/g, '')) : null;
      })
      .filter(p => typeof p === 'number' && !isNaN(p));

    // Check if prices are sorted ascendingly
    let isSorted = false;
    if (prices.length > 1) {
      isSorted = prices.every((v, i, arr) => i === 0 || arr[i - 1] <= v);
    }

    return {
      success: true,
      action: 'Applied "Price: Low to High" sort filter after searching for products. Verified sort order.',
      extractedData: {
        pricesExtracted: prices,
        isSortedAscending: isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
