import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'laptop');
    await page.click('.search-btn');

    // Wait for search results to load
    await page.waitForSelector('.products-grid', { timeout: 10000 });
    await page.waitForTimeout(2000); // Additional wait for results to stabilize

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Apply price sort filter
    await page.waitForSelector('#sortSelect');
    await page.selectOption('#sortSelect', { label: 'Price: Low to High' });

    // Wait for sorted results to load
    await page.waitForTimeout(3000); // Wait for resorting to complete
    await page.waitForSelector('.products-grid .product-card');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract product data to verify sorting
    const productData = await page.$$eval('.product-card', products => 
      products.slice(0, 5).map(product => ({
        title: product.querySelector('.product-title')?.textContent?.trim() || '',
        price: product.querySelector('.price')?.textContent?.trim() || ''
      }))
    );

    return {
      success: true,
      action: "Applied Price: Low to High sort filter to search results",
      extractedData: {
        sortedProducts: productData,
        productCount: productData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Wait for and locate the sort dropdown using multiple strategies
    const sortDropdown = await page.waitForSelector('#sortSelect, select.search-category, select[name="undefined"]', { timeout: 5000 });

    // Select the price sort option
    await sortDropdown.selectOption({ label: 'Price: Low to High' });
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for UI update

    // Verify the sort was applied by checking product prices
    const productPrices = await page.$$eval('.product-card .price, .products-grid .price', 
      elements => elements.map(el => parseFloat(el.textContent.replace('$', ''))));

    // Check if prices are in ascending order
    const isSorted = productPrices.every((price, i, arr) => i === 0 || price >= arr[i - 1]);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    return {
      success: true,
      action: "Successfully searched for products and applied 'Price: Low to High' sort filter",
      extractedData: {
        productCount: productPrices.length,
        prices: productPrices,
        isSorted: isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

