import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'laptop');
    await page.click('button.search-btn');

    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Wait for and locate the sort dropdown
    await page.waitForSelector('#sortSelect');

    // Select 'Price: Low to High' option
    await page.selectOption('#sortSelect', { label: 'Price: Low to High' });

    // Wait for results to update
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for UI update

    // Take screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract product prices to verify sorting
    const prices = await page.$$eval('.product-card .price', elements => 
      elements.map(el => parseFloat(el.textContent.replace('$', '')))
    );

    // Verify prices are sorted low to high
    const isSorted = prices.every((price, i, arr) => 
      i === 0 || price >= arr[i - 1]
    );

    return {
      success: true,
      action: "Applied 'Price: Low to High' sort filter to search results",
      extractedData: {
        prices: prices.slice(0, 5), // First 5 prices
        isSorted: isSorted,
        productCount: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products using the search input
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');

    // Wait for search results to load
    await page.waitForSelector('.products-grid');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Find and interact with sort dropdown using multiple selector strategies
    const sortSelect = await page.$('select#sortSelect') || 
                      await page.$('select[class*="sort"]') ||
                      await page.$('select[data-testid*="sort"]');

    if (!sortSelect) {
      throw new Error('Sort dropdown not found');
    }

    // Select 'Price: Low to High' option
    await sortSelect.selectOption({ label: 'Price: Low to High' });

    // Wait for products to re-sort
    await page.waitForTimeout(2000);
    await page.waitForSelector('.products-grid .product-card');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_sort.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_sort.png'));

    // Extract product data to verify sorting
    const productPrices = await page.$$eval('.products-grid .product-card .product-price', 
      prices => prices.map(p => parseFloat(p.textContent.replace(/[^\d.]/g, ''))));

    // Verify prices are sorted in ascending order
    const isSorted = productPrices.every((price, i, arr) => 
      i === 0 || price >= arr[i - 1]);

    if (!isSorted) {
      throw new Error('Products are not sorted by price low to high');
    }

    return {
      success: true,
      action: "Successfully searched for 'electronics' and sorted by Price: Low to High",
      extractedData: {
        productCount: productPrices.length,
        prices: productPrices,
        isPriceSorted: isSorted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');

    await page.waitForTimeout(1000);
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Find and interact with sort dropdown using multiple selector strategies
    const sortSelectors = [
        '#sortSelect',
        'select[name="sort"]',
        'select.sort-dropdown',
        'xpath=//select[contains(@class, "sort")]',
        'xpath=//select[.//option[contains(text(), "Price")]]'
    ];

    let sortDropdown = null;
    for (const selector of sortSelectors) {
        sortDropdown = await page.$(selector);
        if (sortDropdown) break;
    }

    if (!sortDropdown) {
        throw new Error('Sort dropdown not found');
    }

    // Select price low to high option
    await page.selectOption(sortDropdown, { 
        value: await page.$eval(`${sortSelectors.find(s => s === sortDropdown._selector)} option:has-text("Price: Low to High")`, 
        el => el.value) 
    });

    await page.waitForTimeout(2000);
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_results.png'));

    // Extract product data to verify sort
    const products = await page.$$eval('.product-card', cards => 
        cards.slice(0, 5).map(card => ({
            title: card.querySelector('.product-title')?.textContent?.trim() || '',
            price: card.querySelector('.price')?.textContent?.trim() || ''
        }))
    );

    return {
      success: true,
      action: "Successfully searched for electronics and sorted by price low to high",
      extractedData: { products },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete price sort task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
