import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');

    // Wait for search results to load
    await page.waitForSelector('.products-grid .product-card');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Wait for sort dropdown and apply price filter
    await page.waitForSelector('#sortSelect');
    await page.selectOption('#sortSelect', { label: 'Price: Low to High' });

    // Wait for products to re-sort
    await page.waitForTimeout(2000); // Allow time for sorting animation
    await page.waitForSelector('.products-grid .product-card');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_sort.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_sort.png'));

    // Extract product prices to verify sorting
    const prices = await page.$$eval('.product-card .price', nodes => 
      nodes.map(n => parseFloat(n.innerText.replace('$', '')))
    );

    // Verify prices are sorted low to high
    const isSorted = prices.every((price, i, arr) => 
      i === 0 || price >= arr[i - 1]
    );

    return {
      success: true,
      action: "Applied price sort filter (Low to High) to search results",
      extractedData: {
        sortedPrices: prices,
        isCorrectlySorted: isSorted,
        productCount: prices.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for products
    const searchInput = await page.waitForSelector('#searchInput');
    await searchInput.fill('electronics');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Click search button
    const searchBtn = await page.waitForSelector('button.search-btn');
    await searchBtn.click();
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Wait for sort dropdown and apply price sort
    const sortDropdown = await page.waitForSelector('select[name="sort"]', { timeout: 5000 }).catch(async () => {
      // Alternative selector if first one fails
      return await page.waitForSelector('#sortSelect');
    });

    await sortDropdown.selectOption({ label: 'Price: Low to High' });
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for sorting to complete

    // Take screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_sorted_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_sorted_results.png'));

    // Extract product data to verify sorting
    const products = await page.$$eval('.products-grid .product-card', cards => 
      cards.map(card => ({
        title: card.querySelector('.product-title')?.textContent?.trim() || '',
        price: parseFloat(card.querySelector('.product-price')?.textContent?.replace(/[^\d.]/g, '') || 0)
      }))
    );

    // Verify prices are sorted low to high
    const prices = products.map(p => p.price);
    const isSorted = prices.every((price, i, arr) => i === 0 || price >= arr[i - 1]);

    return {
      success: true,
      action: "Applied 'Price: Low to High' sort filter to search results",
      extractedData: {
        products: products.slice(0, 5), // First 5 products
        isPriceSorted: isSorted,
        productCount: products.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to apply price sort filter",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

