    const isMenuVisible = await page.isVisible('#mobileMenuOverlay');

    return {
      success: isMenuVisible,
      action: "Clicked mobile menu button and opened mobile navigation menu",
      extractedData: {
        menuVisible: isMenuVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
