import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the mobile menu button (hamburger icon)
    await page.waitForSelector('.nav-menu'); // Wait for the mobile menu button to be visible
    await page.click('.nav-menu'); // Click the mobile menu button

    // Take screenshot after clicking the mobile menu
    await page.waitForSelector('#mobileMenuOverlay'); // Wait for the mobile menu overlay to appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_mobile_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_mobile_menu_open.png'));

    // Verify that the mobile menu is open by checking for an element within the menu
    const isMenuOpen = await page.$eval('#mobileMenuOverlay', el => el.style.display !== 'none');

    return {
      success: true,
      action: "Opened the mobile navigation menu successfully",
      extractedData: { isMenuOpen },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

