import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the mobile menu button (hamburger icon)
    await page.waitForSelector('.nav-menu');
    await page.click('.nav-menu');

    // Wait for the mobile menu overlay to be visible
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible' });

    // Take screenshot after opening the mobile menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_mobile_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_mobile_menu_opened.png'));

    // Verify that the mobile menu is opened by checking for a specific element
    const isMenuVisible = await page.isVisible('#mobileMenuOverlay');

    return {
      success: true,
      action: "Opened the mobile navigation menu",
      extractedData: { isMenuVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

