import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu button (hamburger icon) to be visible
    await page.waitForSelector('.nav-menu');

    // Click the mobile menu button
    await page.click('.nav-menu');

    // Wait for the mobile menu overlay to be visible
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible' });

    // Take a screenshot of the mobile menu opened
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_mobile_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_mobile_menu_opened.png'));

    return {
      success: true,
      action: "Mobile menu opened successfully",
      extractedData: {
        menuVisible: await page.isVisible('#mobileMenuOverlay')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
