import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set mobile viewport to trigger mobile menu visibility
    await page.setViewportSize({ width: 375, height: 667 });

    // Wait for and click the mobile menu button (hamburger icon)
    const menuButton = await page.waitForSelector('.nav-menu', { state: 'visible' });
    await menuButton.click();

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible' });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify menu is open
    const menuItems = await page.$$eval('#mobileMenuOverlay a', 
      items => items.slice(0, 5).map(item => ({
        text: item.textContent.trim(),
        href: item.href
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems,
        menuVisible: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const menuButton = await page.waitForSelector('.nav-menu, [class*="menu"], [onclick*="toggleMobileMenu"]');
    await menuButton.click();

    // Wait for mobile menu to appear - using overlay selector with timeout
    await page.waitForSelector('#mobileMenuOverlay, [id*="mobile"], [class*="overlay"]', { 
      state: 'visible', 
      timeout: 5000 
    });

    // Take screenshot after menu opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Verify menu is open by checking visibility of menu items
    const menuItems = await page.$$eval('a:contains("Your Profile"), a:contains("Your Orders"), a:contains("Your Wish List")', 
      items => items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        visibleMenuItems: menuItems,
        menuItemsCount: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const mobileMenuButton = await page.waitForSelector('.nav-menu', { state: 'visible' });
    await mobileMenuButton.click();

    // Wait for mobile menu to open by checking for close button
    await page.waitForSelector('button.close-mobile-menu', { state: 'visible', timeout: 5000 });

    // Take screenshot after menu opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Verify menu is open by checking visibility of menu items
    const menuItems = await page.$$eval('.nav-links a', items => 
      items.map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    const visibleItems = menuItems.filter(item => item.visible);

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: visibleItems.map(item => item.text),
        totalVisibleItems: visibleItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
