import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Wait for and click the mobile menu button
    await page.waitForSelector('.nav-menu', { state: 'visible' });
    await page.click('.nav-menu');

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible', timeout: 5000 });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Extract menu items to verify successful opening
    const menuItems = await page.$$eval('#mobileMenuOverlay a', 
      items => items.slice(0, 5).map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Clicked mobile menu button and opened navigation menu",
      extractedData: {
        menuItems: menuItems
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the mobile menu button and click it
    const menuButton = await page.waitForSelector('.nav-menu', { timeout: 5000 });
    await menuButton.click();

    // Wait for mobile menu overlay to appear using multiple strategies
    await Promise.race([
      page.waitForSelector('#mobileMenuOverlay', { state: 'visible', timeout: 3000 }),
      page.waitForSelector('.mobile-menu', { state: 'visible', timeout: 3000 }),
      page.waitForFunction(() => {
        const overlay = document.querySelector('#mobileMenuOverlay');
        return overlay && window.getComputedStyle(overlay).display !== 'none';
      }, { timeout: 3000 })
    ]);

    // Take screenshot after menu opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Verify menu is open by checking for menu items
    const menuItems = await page.$$eval('.nav-links a', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Successfully clicked mobile menu button and opened navigation menu",
      extractedData: {
        menuItems: menuItems,
        menuItemCount: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

