import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button (hamburger icon)
    const mobileMenuButton = await page.waitForSelector('.nav-menu');
    await mobileMenuButton.click();

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible' });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify navigation menu content
    const menuItems = await page.$$eval('#mobileMenuOverlay a', elements => 
      elements.map(el => ({
        text: el.textContent.trim(),
        href: el.href
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.slice(0, 8), // First 8 menu items
        totalMenuItems: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to trigger mobile layout
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const menuButton = await page.waitForSelector('.nav-menu, [class*="menu"], [class*="hamburger"], .fa-bars', { state: 'visible' });
    await menuButton.click();

    // Wait for mobile menu to appear using multiple possible selectors
    await page.waitForSelector('#mobileMenuOverlay, [id*="menu"], [class*="overlay"], [class*="nav"][class*="links"]', { state: 'visible' });

    // Take screenshot after menu opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify successful opening
    const menuItems = await page.$$eval('#navLinks a, [class*="nav"] a, [class*="menu"] a', 
      items => items.map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.filter(item => item.visible),
        totalItems: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to trigger mobile layout
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button (hamburger icon)
    const mobileMenuButton = await page.waitForSelector('.nav-menu', { timeout: 5000 });
    await mobileMenuButton.click();

    // Wait for mobile menu to open (check for close button or overlay)
    await Promise.race([
      page.waitForSelector('button.close-mobile-menu', { timeout: 3000 }),
      page.waitForSelector('#mobileMenuOverlay', { timeout: 3000 })
    ]);

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify success
    const menuItems = await page.$$eval('#mobileMenuOverlay a, .nav-links a', 
      elements => elements.map(el => el.textContent.trim()).filter(text => text.length > 0)
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.slice(0, 10), // First 10 menu items
        menuItemCount: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
