import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Wait for and click the mobile menu button (hamburger icon)
    await page.waitForSelector('.nav-menu', { timeout: 5000 });
    await page.click('.nav-menu');

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { 
      state: 'visible', 
      timeout: 5000 
    });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify navigation menu content
    const menuItems = await page.$$eval('#mobileMenuOverlay a', elements => 
      elements.map(el => ({
        text: el.textContent.trim(),
        href: el.getAttribute('href')
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.slice(0, 5) // Return first 5 menu items
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
