    const mobileMenuButton = await page.waitForSelector('.nav-menu', { state: 'visible' });
    await mobileMenuButton.click();

    // Wait for mobile menu to open - check for overlay or visible menu items
    await Promise.race([
      page.waitForSelector('#mobileMenuOverlay', { state: 'visible' }),
      page.waitForSelector('.nav-links', { state: 'visible' }),
      page.waitForTimeout(1000) // Fallback timeout
    ]);

    // Take screenshot after menu interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Verify menu is open by checking for visible navigation items
    const navLinks = await page.$$eval('.nav-links li', items => 
      items.map(item => item.textContent?.trim()).filter(Boolean)
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: navLinks,
        menuItemsCount: navLinks.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const mobileMenuButton = await page.waitForSelector('.nav-menu, [class*="menu"], [onclick*="toggleMobileMenu"]', { timeout: 5000 });
    await mobileMenuButton.click();

    // Wait for mobile menu to open - check for overlay or expanded menu
    await Promise.race([
      page.waitForSelector('#mobileMenuOverlay', { timeout: 3000 }),
      page.waitForSelector('.nav-links[style*="visible"], .nav-links[style*="block"]', { timeout: 3000 }),
      page.waitForFunction(() => {
        const menu = document.querySelector('.nav-links');
        return menu && (menu.offsetHeight > 0 || window.getComputedStyle(menu).display !== 'none');
      }, { timeout: 3000 })
    ]);

    // Take screenshot after menu opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify successful opening
    const menuItems = await page.$$eval('.nav-links a', items => 
      items.map(item => item.textContent.trim())
    );

    return {
      success: true,
      action: "Clicked mobile menu button and opened navigation menu",
      extractedData: {
        menuItems: menuItems,
        menuItemCount: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
