import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to trigger mobile menu
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button
    await page.waitForSelector('.nav-menu', { state: 'visible' });
    await page.click('.nav-menu');

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible' });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify navigation menu
    const menuItems = await page.$$eval('#mobileMenuOverlay a', links => 
      links.map(link => link.textContent.trim())
    );

    return {
      success: true,
      action: "Successfully clicked mobile menu button and opened navigation menu",
      extractedData: {
        menuItems: menuItems.slice(0, 5) // First 5 menu items
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

