import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to trigger mobile menu
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial_mobile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial_mobile.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const menuSelectors = [
      '.nav-menu',
      'div[onclick*="toggleMobileMenu"]',
      'div:has(i.fa-bars)',
      'xpath=//div[contains(@class, "nav-menu") and contains(@onclick, "toggleMobileMenu")]'
    ];

    let menuClicked = false;
    for (const selector of menuSelectors) {
      try {
        await page.waitForSelector(selector, { timeout: 3000 });
        await page.click(selector);
        menuClicked = true;
        break;
      } catch (error) {
        continue;
      }
    }

    if (!menuClicked) {
      throw new Error('Could not find mobile menu button with any selector');
    }

    // Wait for mobile menu to open by checking for overlay or menu items
    await Promise.race([
      page.waitForSelector('#mobileMenuOverlay', { timeout: 5000 }),
      page.waitForSelector('.nav-links:visible', { timeout: 5000 }),
      page.waitForSelector('button.close-mobile-menu', { timeout: 5000 })
    ]);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Extract menu items to verify menu is open
    const menuItems = await page.$$eval('.nav-links a', elements => 
      elements.map(el => el.textContent.trim())
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.slice(0, 5), // First 5 menu items
        menuItemCount: menuItems.length,
        overlayVisible: await page.$eval('#mobileMenuOverlay', el => el.checkVisibility()),
        closeButtonVisible: await page.$eval('button.close-mobile-menu', el => el.checkVisibility())
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
