import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set mobile viewport to ensure mobile menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Wait for and click the mobile menu button (hamburger icon)
    const mobileMenuButton = await page.waitForSelector('.nav-menu', { timeout: 5000 });
    await mobileMenuButton.click();

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible', timeout: 5000 });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify menu content
    const menuItems = await page.$$eval('#mobileMenuOverlay a', links => 
      links.map(link => ({
        text: link.textContent.trim(),
        href: link.href
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.slice(0, 8) // First 8 menu items
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
      screenshots.push(path.join(screenshotsDir, 'step_error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to ensure hamburger menu is visible
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const menuButton = await page.waitForSelector('.nav-menu, [class*="menu"], [class*="hamburger"], i.fa-bars', {
      state: 'visible',
      timeout: 5000
    });

    await menuButton.click();

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay, [id*="menu"], [class*="overlay"]', {
      state: 'visible',
      timeout: 3000
    });

    // Take screenshot after menu opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify successful opening
    const menuItems = await page.$$eval(
      '#mobileMenuOverlay a, .nav-menu a, [class*="menu"] a',
      items => items.map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.filter(item => item.visible),
        menuItemsCount: menuItems.filter(item => item.visible).length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

