    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Set mobile viewport to trigger mobile menu
    await page.setViewportSize({ width: 375, height: 667 });

    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    const mobileMenuButton = await page.waitForSelector('.nav-menu, [onclick*="toggleMobileMenu"], .fa-bars', { state: 'visible' });
    await mobileMenuButton.click();

    // Wait for mobile menu to open - check for either overlay or close button
    await Promise.race([
      page.waitForSelector('#mobileMenuOverlay', { state: 'visible' }),
      page.waitForSelector('button.close-mobile-menu', { state: 'visible' }),
      page.waitForSelector('.mobile-nav-open', { state: 'visible' })
    ]);

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify successful opening
    const menuItems = await page.$$eval('#mobileMenuOverlay a, .mobile-menu a, [class*="mobile"] a', 
      items => items.slice(0, 5).map(item => ({
        text: item.textContent.trim(),
        visible: item.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems,
        menuOpen: true,
        itemsCount: menuItems.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');

    // Set mobile viewport to trigger mobile menu
    await page.setViewportSize({ width: 375, height: 667 });

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the mobile menu button using multiple selector strategies
    await page.waitForSelector('.nav-menu', { state: 'visible' });
    await page.click('.nav-menu');

    // Wait for mobile menu overlay to appear
    await page.waitForSelector('#mobileMenuOverlay', { state: 'visible' });

    // Take screenshot after opening menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Extract menu items to verify menu opened successfully
    const menuItems = await page.$$eval('#mobileMenuOverlay a', elements => 
      elements.map(el => ({
        text: el.textContent.trim(),
        visible: el.offsetParent !== null
      }))
    );

    return {
      success: true,
      action: "Successfully opened mobile navigation menu by clicking hamburger icon",
      extractedData: {
        menuItems: menuItems.filter(item => item.visible).map(item => item.text),
        menuItemCount: menuItems.filter(item => item.visible).length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to open mobile navigation menu",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
