import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Hover over the account & lists dropdown to reveal options
    await page.waitForSelector('.account-lists');
    await page.hover('.account-lists');

    // Take screenshot after hovering
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hovered_account_lists.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hovered_account_lists.png'));

    // Click on 'Your Profile' link
    await page.waitForSelector('a[onclick*="showProfile"]');
    await page.click('a[onclick*="showProfile"]');

    // Take screenshot after clicking
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_navigated_to_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_navigated_to_profile.png'));

    // Confirm navigation by checking for a unique element on the profile page
    const profileHeader = await page.waitForSelector('h1:contains("Jun Kim")', { timeout: 5000 });
    const profileHeaderText = await profileHeader.innerText();

    return {
      success: true,
      action: "Navigated to Jun's profile successfully",
      extractedData: { profileHeader: profileHeaderText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
