import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the account dropdown
    await page.waitForSelector('.account-lists');
    await page.click('.account-lists');

    // Wait for the dropdown content to be visible
    await page.waitForSelector('.dropdown-content');

    // Click on 'Your Profile'
    await page.click('a[onclick*="showProfile"]');

    // Wait for the profile page to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    return {
      success: true,
      action: "Navigated to Jun's profile page",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

