import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the account dropdown
    await page.waitForSelector('.account-lists');
    await page.click('.account-lists');

    // Wait for the dropdown content to be visible
    await page.waitForSelector('.dropdown-content');

    // Click on 'Your Profile' link
    await page.click('a:contains("Your Profile")');

    // Wait for the profile page to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    return {
      success: true,
      action: "Navigated to Jun's profile page",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the account dropdown to be visible
    await page.waitForSelector('.account-lists', { timeout: 5000 });

    // Click on the account dropdown to reveal the options
    await page.click('.account-lists');

    // Wait for the profile link to be visible and click it
    await page.waitForSelector('a[onclick*="showProfile"]', { timeout: 5000 });
    await page.click('a[onclick*="showProfile"]');

    // Take a screenshot after navigating to Jun's profile
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_navigated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_navigated.png'));

    // Extract meaningful data to verify navigation success
    const pageTitle = await page.title();
    const isOnProfilePage = pageTitle.includes("Jun's Profile") || page.url().includes("/profile");

    return {
      success: isOnProfilePage,
      action: "Navigated to Jun's Profile page",
      extractedData: { pageTitle },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

