import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and hover over account dropdown to make it visible
    await page.waitForSelector('.account-lists');
    await page.hover('.account-lists');

    // Wait for dropdown content to appear and take screenshot
    await page.waitForSelector('.dropdown-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Click on "Your Profile" link using multiple selector strategies
    const profileSelector = [
      'a[onclick*="showProfile"]',
      'text="Your Profile"',
      '.dropdown-content a:has-text("Your Profile")'
    ].join(', ');

    await page.click(profileSelector);

    // Wait for profile page to load and verify navigation
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for any animations

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile data to verify successful navigation
    const profileData = await page.evaluate(() => {
      return {
        title: document.title,
        profileName: document.querySelector('.profile-name')?.textContent?.trim(),
        hasProfileContent: !!document.querySelector('.profile-container')
      };
    });

    return {
      success: true,
      action: "Successfully navigated to Jun's profile page",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

