import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for account dropdown to be visible
    await page.waitForSelector('.account-lists', { state: 'visible' });

    // Hover over account dropdown to trigger the menu
    await page.hover('.account-lists');

    // Wait for dropdown content to appear
    await page.waitForSelector('.dropdown-content', { state: 'visible' });

    // Take screenshot after hover
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Click on "Your Profile" using multiple selector strategies
    const profileSelector = 'a[onclick*="showProfile"], text="Your Profile"';
    await page.waitForSelector(profileSelector, { state: 'visible' });
    await page.click(profileSelector);

    // Wait for navigation to complete
    await page.waitForLoadState('networkidle');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile data to verify completion
    const extractedData = await page.evaluate(() => {
      const profileElements = document.querySelectorAll('[class*="profile"], [id*="profile"]');
      const profileInfo = {};

      profileElements.forEach(el => {
        if (el.textContent && el.textContent.trim()) {
          const key = el.className || el.id || 'profile_info';
          profileInfo[key] = el.textContent.trim();
        }
      });

      return profileInfo;
    });

    return {
      success: true,
      action: "Successfully navigated to Jun's profile by clicking account dropdown and selecting 'Your Profile'",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to navigate to Jun's profile",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}
