import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for account dropdown to be available
    await page.waitForSelector('.account-lists', { timeout: 5000 });

    // Hover over account dropdown to trigger the menu
    await page.hover('.account-lists');

    // Wait for dropdown content to become visible
    await page.waitForSelector('.dropdown-content', { state: 'visible', timeout: 3000 });

    // Take screenshot after hovering
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_visible.png'));

    // Click on "Your Profile" using multiple selector strategies
    const profileLink = await page.$('a[onclick*="showProfile"]');

    if (profileLink) {
      await profileLink.click();
    } else {
      // Alternative selector strategy using text content
      await page.click('text="Your Profile"');
    }

    // Wait for profile page to load
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for content to render

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile information to verify success
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.username')?.textContent || '';
      const fullName = document.querySelector('.full-name')?.textContent || '';
      return { username, fullName };
    });

    return {
      success: true,
      action: "Successfully navigated to Jun's Profile by clicking account dropdown and selecting 'Your Profile'",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error_state.png') });
      screenshots.push(path.join(screenshotsDir, 'error_state.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to navigate to Jun's Profile",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
