import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for account dropdown to be visible
    await page.waitForSelector('.account-lists', { state: 'visible' });

    // Hover over account dropdown to trigger the menu
    await page.hover('.account-lists');

    // Wait for dropdown content to appear
    await page.waitForSelector('.dropdown-content', { state: 'visible' });

    // Take screenshot after dropdown appears
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_visible.png'));

    // Click on "Your Profile" using multiple selector strategies
    await page.click('a[onclick*="showProfile"]');

    // Wait for profile page to load
    await page.waitForLoadState('networkidle');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile data to verify success
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.profile-username')?.textContent;
      const fullName = document.querySelector('.profile-fullname')?.textContent;
      const email = document.querySelector('.profile-email')?.textContent;

      return {
        username: username?.trim(),
        fullName: fullName?.trim(),
        email: email?.trim()
      };
    });

    return {
      success: true,
      action: "Successfully navigated to Jun's profile page",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {
      // Ignore screenshot errors
    }

    return {
      success: false,
      action: "Failed to navigate to Jun's profile",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
