import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for account dropdown to be visible
    await page.waitForSelector('.account-lists', { state: 'visible' });

    // Hover over account dropdown to trigger the menu
    await page.hover('.account-lists');

    // Wait for dropdown content to be visible
    await page.waitForSelector('.dropdown-content', { state: 'visible' });

    // Take screenshot after hover
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_visible.png'));

    // Click on "Your Profile" link using multiple selector strategies
    const profileLink = await page.waitForSelector('a[onclick*="showProfile"], text="Your Profile"', { state: 'visible' });
    await profileLink.click();

    // Wait for profile page to load
    await page.waitForLoadState('networkidle');

    // Verify we're on the profile page by checking for profile-specific content
    await page.waitForSelector(':text("Jun Kim"), :text("Profile")', { timeout: 5000 });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile data to verify success
    const profileData = await page.evaluate(() => {
      const nameElement = document.querySelector('h1, h2, .profile-name');
      const emailElement = document.querySelector('.email, [class*="email"]');

      return {
        profileName: nameElement?.textContent?.trim() || 'Not found',
        profileEmail: emailElement?.textContent?.trim() || 'Not found',
        pageTitle: document.title
      };
    });

    return {
      success: true,
      action: "Successfully navigated to Jun's Profile by clicking account dropdown and selecting 'Your Profile'",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to navigate to Jun's Profile",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
