import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and hover over account dropdown to reveal menu
    const accountDropdown = await page.waitForSelector('.account-lists');
    await accountDropdown.hover();

    // Wait for dropdown menu to become visible and take screenshot
    await page.waitForSelector('.dropdown-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Click on "Your Profile" link using multiple selector strategies
    const profileLink = await page.waitForSelector('a[onclick*="showProfile"]');
    await profileLink.click();

    // Wait for profile page to load and verify navigation
    await page.waitForLoadState('networkidle');

    // Extract profile information to verify successful navigation
    const profileData = await page.evaluate(() => {
      const profileSection = document.querySelector('.profile-section');
      return {
        username: profileSection ? profileSection.querySelector('h1')?.innerText : null,
        joinDate: profileSection ? profileSection.querySelector('.join-date')?.innerText : null,
        ordersCount: profileSection ? profileSection.querySelector('.orders-count')?.innerText : null
      };
    });

    // Take final screenshot of profile page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    return {
      success: true,
      action: "Successfully navigated to Jun's profile page by clicking account dropdown and selecting 'Your Profile'",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

