import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for account dropdown to be available and click it
    await page.waitForSelector('.account-lists', { state: 'visible' });
    await page.click('.account-lists');

    // Wait for dropdown content to appear
    await page.waitForSelector('.dropdown-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Click on "Your Profile" using multiple selector strategies
    const profileLink = await page.waitForSelector('a[onclick*="showProfile"]', { state: 'visible' });
    await profileLink.click();

    // Wait for profile page to load and verify navigation
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for any animations

    // Verify we're on the profile page by checking for profile elements
    await page.waitForSelector('.profile-header', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_profile_page.png'));

    // Extract profile information
    const profileData = await page.evaluate(() => {
      const nameEl = document.querySelector('.profile-name');
      const emailEl = document.querySelector('.profile-email');

      return {
        profileName: nameEl ? nameEl.textContent.trim() : 'Not found',
        profileEmail: emailEl ? emailEl.textContent.trim() : 'Not found',
        success: !!nameEl && !!emailEl
      };
    });

    return {
      success: true,
      action: "Successfully navigated to Jun's profile by clicking account dropdown and selecting 'Your Profile'",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
    screenshots.push(path.join(screenshotsDir, 'step_error.png'));

    return {
      success: false,
      action: "Failed to navigate to Jun's profile",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
