import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to be visible and click it to open the product detail modal
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');

    // Wait for the modal to load
    await page.waitForSelector('.modal-content'); // Assuming .modal-content is the selector for the modal

    // Take a screenshot of the product detail modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Click the 'Add to Wish List' button
    await page.waitForSelector('.add-to-wishlist');
    await page.click('.add-to-wishlist');

    // Take a screenshot after adding to wishlist
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_wishlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_wishlist.png'));

    // Extract confirmation message or any relevant data
    const confirmationMessage = await page.evaluate(() => {
      return document.querySelector('.wishlist-confirmation').innerText; // Assuming there's a confirmation message
    });

    return {
      success: true,
      action: "Product added to wishlist successfully",
      extractedData: { confirmationMessage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to be visible
    await page.waitForSelector('.product-card:first-child');

    // Click on the first product card to open the product detail modal
    await page.click('.product-card:first-child');

    // Wait for the modal to appear
    await page.waitForSelector('.modal-product-details', { visible: true });

    // Take a screenshot of the product modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Click the 'Add to Wish List' button
    await page.waitForSelector('.add-to-wishlist');
    await page.click('.add-to-wishlist');

    // Verify if the item has been added to the wishlist
    const successMessage = await page.waitForSelector('.wishlist-success-message', { timeout: 5000 });
    const message = await successMessage.evaluate(el => el.textContent);

    // Take a final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_confirmation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_confirmation.png'));

    return {
      success: true,
      action: "Successfully added product to wishlist",
      extractedData: { message },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

