import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to be visible
    await page.waitForSelector('.product-card:first-child');

    // Click on the first product card to open the detail modal
    await page.click('.product-card:first-child');

    // Wait for the modal to be visible
    await page.waitForSelector('.product-detail-modal', { state: 'visible' });

    // Take a screenshot of the product detail modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_detail_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_detail_modal.png'));

    // Click on the 'Add to Wish List' button
    await page.click('.add-to-wishlist');

    // Wait for a confirmation message or change of state indicating the product was added
    await page.waitForSelector('.wishlist-confirmation', { state: 'visible' });

    // Take a final screenshot to confirm the action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_confirmation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_confirmation.png'));

    return {
      success: true,
      action: "Product successfully added to the wishlist",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
