import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first product card to open the product detail modal
    await page.waitForSelector('.product-card:first-child', { timeout: 5000 });
    await page.click('.product-card:first-child');
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the product detail modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Click the 'Add to Wish List' button
    await page.waitForSelector('.add-to-wishlist', { timeout: 5000 });
    await page.click('.add-to-wishlist');

    // Take a screenshot after adding to the wishlist
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_wishlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_wishlist.png'));

    // Verify the action by checking for confirmation message or updated UI state
    const confirmationSelector = 'text=Added to Your Wish List'; // Example confirmation text
    const isSuccess = await page.waitForSelector(confirmationSelector, { timeout: 5000 })
      .then(() => true)
      .catch(() => false);

    return {
      success: isSuccess,
      action: "Added product to wishlist successfully",
      extractedData: { confirmation: isSuccess },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
