import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to be visible and click on it to open the product detail modal
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');

    // Wait for the modal to load
    await page.waitForSelector('.modal-content'); // Assuming there's a modal with this class

    // Take a screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Click the 'Add to Wish List' button
    await page.waitForSelector('.add-to-wishlist');
    await page.click('.add-to-wishlist');

    // Take a final screenshot after adding to wishlist
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_wishlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_wishlist.png'));

    return {
      success: true,
      action: "Product added to wishlist successfully",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to be visible and click it to open the product detail modal
    await page.waitForSelector('.product-card:first-child', { timeout: 5000 });
    await page.click('.product-card:first-child');

    // Wait for the 'Add to Wish List' button to be visible and click it
    await page.waitForSelector('.add-to-wishlist', { timeout: 5000 });
    await page.click('.add-to-wishlist');

    // Take a screenshot after adding the product to the wishlist
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_wishlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_wishlist.png'));

    // Verify that the product has been added to the wishlist by checking for a confirmation message or similar indication
    const confirmationMessage = await page.waitForSelector('.wishlist-confirmation', { timeout: 5000 });
    const extractedData = {
      confirmationText: await confirmationMessage.textContent()
    };

    return {
      success: true,
      action: "Product successfully added to the wishlist",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

