import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the first product card
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');

    // Wait for product detail modal to appear
    await page.waitForSelector('.modal-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract product information before adding to wishlist
    const productInfo = await page.evaluate(() => {
      const title = document.querySelector('.product-title')?.innerText || '';
      const price = document.querySelector('.product-price')?.innerText || '';
      return { title, price };
    });

    // Click the Add to Wish List button
    await page.waitForSelector('.add-to-wishlist');
    await page.click('.add-to-wishlist');

    // Wait for confirmation and take final screenshot
    await page.waitForTimeout(1000); // Wait for animation/confirmation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_added.png'));

    return {
      success: true,
      action: "Successfully opened product modal and added item to wishlist",
      extractedData: {
        productTitle: productInfo.title,
        productPrice: productInfo.price,
        wishlistAction: "Item added to wishlist"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

