  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first product card
    await page.waitForSelector('.product-card:first-child', { timeout: 5000 });
    await page.click('.product-card:first-child');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal_opened.png'));

    // Wait for and click the Add to Wishlist button
    await page.waitForSelector('.add-to-wishlist', { timeout: 3000 });
    await page.click('.add-to-wishlist');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_wishlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_wishlist.png'));

    // Extract product details for verification
    const productDetails = await page.evaluate(() => {
      const productCard = document.querySelector('.product-card:first-child');
      return {
        title: productCard?.querySelector('.product-title')?.textContent?.trim(),
        price: productCard?.querySelector('.product-price')?.textContent?.trim(),
        addedToWishlist: document.querySelector('.add-to-wishlist')?.classList?.contains('added') || false
      };
    });

    return {
      success: true,
      action: "Successfully opened product modal and added item to wishlist",
      extractedData: productDetails,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

