import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the first product card
    const productCardSelector = '.product-card:first-child';
    await page.waitForSelector(productCardSelector, { state: 'visible' });
    await page.click(productCardSelector);

    // Wait for product detail modal to appear
    await page.waitForTimeout(1000); // Allow animation to complete
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Click Add to Wishlist button using multiple selector strategies
    const wishlistSelectors = [
      '.add-to-wishlist',
      'button:has-text("Add to Wish List")',
      'button:has-text("Wish List")'
    ];

    let wishlistAdded = false;
    for (const selector of wishlistSelectors) {
      try {
        await page.waitForSelector(selector, { timeout: 2000, state: 'visible' });
        await page.click(selector);
        wishlistAdded = true;
        break;
      } catch (error) {
        continue;
      }
    }

    if (!wishlistAdded) {
      throw new Error('Could not find Add to Wishlist button with any selector');
    }

    // Wait for confirmation and take final screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_added.png'));

    // Extract product name from modal
    const productName = await page.$eval('.product-title, h1, [data-product-name]', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully added product to wishlist",
      extractedData: {
        productName: productName,
        wishlistStatus: "added"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (screenshotError) {
      console.log('Could not capture error screenshot:', screenshotError.message);
    }

    return {
      success: false,
      action: "Failed to add product to wishlist",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
