import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on first product card
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');
    await page.waitForTimeout(1000); // Wait for modal animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal_open.png'));

    // Wait for and click Add to Wishlist button
    await page.waitForSelector('.add-to-wishlist');
    await page.click('.add-to-wishlist');
    await page.waitForTimeout(500); // Wait for wishlist action to complete
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_added.png'));

    // Extract product information from modal
    const productInfo = await page.evaluate(() => {
      const title = document.querySelector('.product-title')?.innerText || 'Unknown';
      const price = document.querySelector('.product-price')?.innerText || 'Unknown';
      return { title, price };
    });

    return {
      success: true,
      action: "Successfully opened product modal and added item to wishlist",
      extractedData: {
        productTitle: productInfo.title,
        productPrice: productInfo.price,
        wishlistAction: "completed"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 10000 });

    // Click the first product card using multiple selector strategies
    const firstProduct = await page.$('.product-card:first-child');
    if (!firstProduct) {
      throw new Error('No product cards found');
    }

    await firstProduct.click();
    await page.waitForTimeout(1000); // Wait for modal animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Wait for and click the wishlist button using multiple strategies
    const wishlistButton = await page.waitForSelector('.add-to-wishlist', { 
      timeout: 5000,
      state: 'visible'
    });

    await wishlistButton.click();
    await page.waitForTimeout(1000); // Wait for wishlist action to complete

    // Verify success by checking if wishlist count updated
    const wishlistCount = await page.$eval('.wishlist-count', el => el.textContent);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_added.png'));

    return {
      success: true,
      action: "Successfully added product to wishlist",
      extractedData: {
        wishlistCount: wishlistCount,
        productAdded: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to add product to wishlist",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

