import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first product card
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');

    // Wait for product modal to appear
    await page.waitForSelector('.product-detail-modal', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Wait for and click the Add to Wishlist button
    await page.waitForSelector('.add-to-wishlist');
    await page.click('.add-to-wishlist');

    // Wait for confirmation and take final screenshot
    await page.waitForTimeout(1000); // Allow time for wishlist animation/confirmation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_wishlist.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_wishlist.png'));

    // Extract product information for verification
    const productInfo = await page.$eval('.product-detail-modal', (modal) => {
      return {
        title: modal.querySelector('.product-title')?.textContent?.trim() || '',
        price: modal.querySelector('.product-price')?.textContent?.trim() || '',
        addedToWishlist: modal.querySelector('.add-to-wishlist')?.textContent?.includes('Added') || false
      };
    });

    return {
      success: true,
      action: "Successfully opened product modal and added item to wishlist",
      extractedData: productInfo,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first product card
    await page.waitForSelector('.product-card');
    const firstProductCard = await page.$('.product-card:first-child');
    await firstProductCard.click();

    // Wait for product detail modal to appear
    await page.waitForSelector('.modal', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Wait for and click the Add to Wishlist button
    await page.waitForSelector('.add-to-wishlist');
    const wishlistButton = await page.$('.add-to-wishlist');
    await wishlistButton.click();

    // Wait for confirmation and take final screenshot
    await page.waitForTimeout(1000); // Allow time for wishlist animation/confirmation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_wishlist_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_wishlist_added.png'));

    // Extract product information for verification
    const productTitle = await page.$eval('.product-card:first-child .product-title', el => el.textContent.trim());
    const productPrice = await page.$eval('.product-card:first-child .product-price', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully opened product modal and added product to wishlist",
      extractedData: {
        productTitle,
        productPrice,
        wishlistStatus: "added"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {
      console.log('Could not take error screenshot:', e.message);
    }

    return {
      success: false,
      action: "Failed to add product to wishlist",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

