import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add an item to the cart
    await page.waitForSelector('.search-input');
    await page.fill('.search-input', 'Apple iPhone 15 Pro Max');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.waitForSelector('text=Apple iPhone 15 Pro Max');
    await page.click('text=Apple iPhone 15 Pro Max');
    await page.waitForSelector('button.add-to-cart'); // Assuming there's an add-to-cart button
    await page.click('button.add-to-cart');
    await page.waitForTimeout(1000); // Wait for the item to be added to the cart

    // Open the cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal'); // Assuming there's a cart modal
    await page.waitForTimeout(1000); // Wait for the cart modal to load

    // Take screenshot of the cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Remove the item from the cart
    await page.waitForSelector('.remove-item'); // Assuming there's a remove button
    await page.click('.remove-item');
    await page.waitForTimeout(1000); // Wait for the item to be removed

    // Take screenshot after removal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_item_removed.png'));

    // Verify the cart is empty
    const cartCount = await page.textContent('#cartCount');
    const isCartEmpty = cartCount === '0';

    return {
      success: true,
      action: "Item removed from cart successfully",
      extractedData: { isCartEmpty },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

