import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Add an item to the cart
    await page.waitForSelector('img[alt="Apple iPhone 15 Pro Max, 256GB, Natural Titanium"]');
    await page.click('img[alt="Apple iPhone 15 Pro Max, 256GB, Natural Titanium"]');
    await page.waitForSelector('button.add-to-cart'); // Wait for the "Add to Cart" button
    await page.click('button.add-to-cart'); // Click to add the item to the cart

    // Take a screenshot after adding the item to the cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Step 2: Open the cart
    await page.waitForSelector('.cart');
    await page.click('.cart'); // Click to open the cart modal

    // Step 3: Wait for the cart modal to be visible
    await page.waitForSelector('.cart-modal', { state: 'visible' });

    // Step 4: Remove the item from the cart
    await page.waitForSelector('.remove-item'); // Wait for the remove button
    await page.click('.remove-item'); // Click the remove button

    // Step 5: Verify the cart is empty or the item is removed
    await page.waitForSelector('#cartCount'); // Wait for cart count to update
    const cartCount = await page.$eval('#cartCount', el => el.innerText);

    // Take a screenshot after removing the item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_item_removed.png'));

    // Return extracted data
    return {
      success: true,
      action: "Removed item from cart successfully",
      extractedData: { cartCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
