import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add an item to the cart (assuming we know the product ID or can select it)
    await page.waitForSelector('button.search-btn');
    await page.click('button.search-btn');
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'Apple iPhone 15 Pro Max');
    await page.keyboard.press('Enter');
    await page.waitForLoadState('networkidle');

    // Take screenshot after searching
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Click on the product to add it to the cart
    await page.waitForSelector('text=Apple iPhone 15 Pro Max, 256GB, Natural Titanium');
    await page.click('text=Apple iPhone 15 Pro Max, 256GB, Natural Titanium');
    await page.waitForLoadState('networkidle');

    // Take screenshot after selecting the product
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_product_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_product_page.png'));

    // Click the 'Add to Cart' button (assuming it has a specific selector)
    await page.waitForSelector('button.add-to-cart');
    await page.click('button.add-to-cart');
    await page.waitForLoadState('networkidle');

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_added_to_cart.png'));

    // Open the cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal'); // Assuming this is the selector for the cart modal
    await page.waitForLoadState('networkidle');

    // Take screenshot of the cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_cart_opened.png'));

    // Click the 'Remove' button for the item in the cart
    await page.waitForSelector('.remove-item'); // Assuming this is the selector for the remove button
    await page.click('.remove-item');
    await page.waitForLoadState('networkidle');

    // Take screenshot after removing the item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_item_removed.png'));

    // Verify that the cart is empty or item count is updated
    const cartCount = await page.textContent('#cartCount');
    const isEmpty = cartCount === '0';

    return {
      success: true,
      action: "Removed item from cart successfully",
      extractedData: { cartCount: cartCount, isEmpty: isEmpty },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

