    await page.click('.remove-item'); // Click to remove the item
    await page.waitForTimeout(1000); // Wait for the removal action to complete

    // Take a screenshot after removing the item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_item_removed.png'));

    // Verify the item is removed by checking cart count
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const success = cartCount === '0'; // Assuming the cart count should be 0 after removal

    return {
      success,
      action: "Removed item from the cart",
      extractedData: { cartCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

