import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add an item to the cart (assuming the first product is added)
    await page.waitForSelector('button.search-btn');
    await page.click('button.search-btn');
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'Apple iPhone 15 Pro Max');
    await page.keyboard.press('Enter');
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Click on the first product to add to cart
    await page.waitForSelector('.product-item'); // Adjust selector based on actual product item
    await page.click('.product-item:first-child'); // Click on the first product
    await page.waitForSelector('button.add-to-cart'); // Wait for the add to cart button
    await page.click('button.add-to-cart'); // Add to cart

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_cart.png'));

    // Open the cart modal
    await page.click('.cart'); // Click on the cart icon
    await page.waitForSelector('.cart-modal'); // Wait for the cart modal to appear

    // Take screenshot of the cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_cart_modal.png'));

    // Click the remove button on the cart item
    await page.waitForSelector('.remove-item'); // Wait for the remove button
    await page.click('.remove-item'); // Click to remove the item

    // Take screenshot after removing the item
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_item_removed.png'));

    // Verify that the cart is empty
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const isCartEmpty = cartCount === '0';

    return {
      success: true,
      action: "Removed item from cart successfully",
      extractedData: { isCartEmpty },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

