import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add an item to the cart
    await page.click('button.search-btn'); // Open search
    await page.type('#searchInput', 'Apple iPhone 15 Pro Max'); // Type search query
    await page.click('button.search-btn'); // Click search button
    await page.waitForSelector('.cart'); // Wait for cart to become visible

    // Wait for the item to be added to cart (this might be a simulated action)
    await page.waitForTimeout(2000); // Simulate some loading time for adding item to cart

    // Open cart modal
    await page.click('.cart'); // Click on cart icon
    await page.waitForSelector('.remove-item'); // Wait for the remove button to be visible

    // Take screenshot of the cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Click the 'Remove' button on the cart item
    await page.click('.remove-item'); // Click on the remove item button
    await page.waitForTimeout(1000); // Wait briefly for removal action to take effect

    // Take final screenshot to verify removal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_item_removed.png'));

    // Verify that the cart count has decreased
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const success = parseInt(cartCount) === 0; // Expecting cart count to be 0 after removal

    return {
      success,
      action: "Removed item from cart",
      extractedData: { cartCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
