import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add an item to the cart
    await page.waitForSelector('.search-input');
    await page.fill('.search-input', 'Apple iPhone 15 Pro Max');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.waitForSelector('.cart');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open the cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal'); // Assuming there's a cart modal that appears
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Click the 'Remove' button on the cart item
    await page.waitForSelector('.remove-item'); // Assuming there's a remove button with this class
    await page.click('.remove-item');
    await page.waitForTimeout(1000); // Wait for the removal action to complete
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    // Verify that the cart is empty or item is removed
    const cartCount = await page.textContent('#cartCount');
    const isEmpty = cartCount === '0';

    return {
      success: true,
      action: "Item removed from cart successfully",
      extractedData: { cartIsEmpty: isEmpty },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

