import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first
    await page.click('a:has-text("Electronics")');
    await page.waitForSelector('.product-card');

    // Click first product's add to cart button
    await page.click('.product-card:first-child .add-to-cart');
    await page.waitForTimeout(1000);

    // Verify cart count updated
    const initialCartCount = await page.textContent('#cartCount');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_item_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_item_added.png'));

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_cart_opened.png'));

    // Remove item from cart
    await page.click('.remove-item');
    await page.waitForTimeout(1000);

    // Verify cart is empty
    const finalCartCount = await page.textContent('#cartCount');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_item_removed.png'));

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        initialCartCount: initialCartCount,
        finalCartCount: finalCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add item to cart first (required for removal)
    await page.waitForSelector('.add-to-cart', { timeout: 5000 });
    await page.click('.add-to-cart');
    await page.waitForTimeout(1000); // Wait for cart update

    // Verify item was added by checking cart count
    const initialCount = await page.$eval('#cartCount', el => el.textContent);
    if (initialCount === '0') {
      throw new Error('Failed to add item to cart');
    }

    // Open cart modal
    await page.click('.cart');
    await page.waitForSelector('.cart-modal', { timeout: 3000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_open.png'));

    // Remove item from cart
    await page.waitForSelector('.remove-item', { timeout: 3000 });
    await page.click('.remove-item');
    await page.waitForTimeout(1000); // Wait for removal animation

    // Verify item was removed
    const finalCount = await page.$eval('#cartCount', el => el.textContent);
    if (finalCount !== '0') {
      throw new Error('Item was not removed from cart');
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_item_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_item_removed.png'));

    return {
      success: true,
      action: "Added item to cart and then removed it successfully",
      extractedData: {
        initialCartCount: initialCount,
        finalCartCount: finalCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to remove item from cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Add an item to cart first
    const productSelector = '.product-card:first-child .add-to-cart';
    await page.waitForSelector(productSelector, { timeout: 5000 });
    await page.click(productSelector);

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 5000 });

    // Open cart modal
    const cartSelector = '.cart';
    await page.waitForSelector(cartSelector);
    await page.click(cartSelector);

    // Wait for cart modal to appear
    const cartModalSelector = '.cart-modal, [class*="cart-modal"], [class*="cartModal"]';
    await page.waitForSelector(cartModalSelector, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal.png'));

    // Click remove button
    const removeButtonSelector = '.remove-item, [class*="remove"], button:has-text("Remove")';
    await page.waitForSelector(removeButtonSelector);
    await page.click(removeButtonSelector);

    // Verify item was removed
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent === '0';
    }, { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_removed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_removed.png'));

    return {
      success: true,
      action: "Successfully added item to cart, opened cart modal, and removed item",
      extractedData: {
        finalCartCount: await page.$eval('#cartCount', el => el.textContent)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to remove item from cart",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
